mapper.select:
SELECT COUNT(*) 
FROM categories
WHERE category_name = #{categoryName}

mapper.update:
UPDATE categories
SET parent_id = #{parentId}
WHERE parent_id = #{categoryId}

mapper.select:
SELECT COUNT(*)
FROM categories
WHERE category_id = #{categoryId}
  AND business_id = #{businessId}
  AND is_deleted = 0

mapper.update:
UPDATE categories
SET is_deleted = 1, updated_at = NOW()
WHERE category_id = #{categoryId}
