mapper.select:
SELECT r.receiving_shipment_no, r.transaction_type, r.created_at, r.status, r.product_id, p.product_name, p.product_description, r.change_quantity, p.product_price, r.warehouse_id, r.memo FROM receiving_shipment r JOIN products p ON r.product_id = p.product_id JOIN warehouses w ON w.warehouse_id = r.warehouse_id JOIN users u on u.created_by = r.created_by
r.created_at >= #{startDate}
AND r.created_at <= #{endDate}
AND (p.product_name LIKE CONCAT('%', #{keyword}, '%') )
AND transaction_type = 'INBOUND' AND u.business_id = #{businessId}
ORDER BY r.receiving_shipment_no desc limit #{cri.startPage},#{cri.pageSize};

SELECT COUNT(*) 
FROM receiving_shipment r
JOIN products p ON r.product_id = p.product_id
JOIN users u on u.created_by = r.created_by


        r.created_at >= #{startDate}


        AND r.created_at <= #{endDate}


        AND p.product_name LIKE CONCAT('%', #{keyword}, '%')

    AND r.transaction_type = 'INBOUND'
    AND u.business_id = #{businessId}

ORDER BY r.receiving_shipment_no desc
