mapper.select:
SELECT p.product_name AS product_name, w.warehouse_name AS warehouse_name, s.total_quantity AS total_quantity, s.reserved_quantity AS reserved_quantity, s.available_stock AS available_stock, s.description AS description, s.updated_at AS updated_at, s.created_at AS created_at FROM stocks s JOIN warehouses w ON s.warehouse_id = w.warehouse_id JOIN products p ON s.product_id = p.product_id WHERE s.business_id = #{criteria.businessId}
AND s.warehouse_id = #{criteria.warehouseId}
AND s.category_name = #{criteria.categoryName}
AND s.total_quantity >= #{criteria.minStock}
AND s.total_quantity <= #{criteria.maxStock}
ORDER BY s.total_quantity ASC
ORDER BY s.total_quantity DESC
ORDER BY s.updated_at DESC
